//import MySqlConnection from "react-native-my-sql-connection";
import React from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  Button,
} from "react-native";

// APP

export default function App() {
  return (
    <SafeAreaView style={styles.body}>
      <View style={styles.container}>
        <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
        <FlatListBasics></FlatListBasics>
        <Button title="Test" />
      </View>
    </SafeAreaView>
  );
}

const FlatListBasics = () => {
  return (
    <View style={styles.usersList}>
      <FlatList
        data={[
          { key: "Jan", isAvailable: true },
          { key: "Daniel", isAvailable: false },
          { key: "Dominik", isAvailable: true },
          { key: "Jerzy", isAvailable: false },
          { key: "Adam", isAvailable: true },
        ]}
        renderItem={({ item }) => (
          <View>
            {item.isAvailable == true ? (
              <Text style={styles.users}>
                {item.key} - <Text style={styles.available}>Dostępny</Text>
              </Text>
            ) : (
              <Text style={styles.users}>
                {item.key} - <Text style={styles.unavailable}>Niedostępny</Text>
              </Text>
            )}
          </View>
        )}
      />
    </View>
  );
};

const MySqlConnection = require("react-native-my-sql-connection");

let config = {
  host: "hostname",
  database: "sys",
  user: "admin",
