import React, { useEffect, useState, Component } from "react";
import { render } from "react-dom";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  Button,
} from "react-native";

// APP

var request = new XMLHttpRequest();

export default function App() {
  const [Users, setUsers] = useState([]);

  const GetList = () => {
    fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
      .then((response) => response.json())
      .then((json) => {
        setUsers(json);
      });
  };

  return (
    <SafeAreaView style={styles.body}>
      <View style={styles.container}>
        <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
        <View style={styles.users}>
          <FlatList
            data={Users}
            renderItem={({ item }) => (
              <View>
                {item.IsAvailable.data.indexOf(1) == 0 ? (
                  <Text style={styles.users}>
                    {item.Name} - <Text style={styles.available}>Dostępny</Text>
                  </Text>
                ) : item.IsAvailable.data.indexOf(0) == 0 ? (
                  <Text style={styles.users}>
                    {item.Name} - {""}
                    <Text style={styles.unavailable}>Niedostępny</Text>
                  </Text>
                ) : (
                  <Text style={styles.users}>
                    Błąd wczytywania użytkowników
                  </Text>
                )}
              </View>
            )}
          />
        </View>
        <View style={styles.refreshbutton}>
          <Button title="Wyświetl/odśwież listę" onPress={GetList} />
        </View>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  body: {
    flex: 1,
    height: "100%",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "dodgerblue",
  },
  container: {
    alignItems: "center",
    height: "80%",
  },
  title: {
    fontSize: "1.25em",
    fontWeight: "bold",
    marginBottom: "5%",
    marginTop: "5%",
  },
  usersList: {
    marginTop: "5%",
    marginBottom: "10px",
  },
  users: {
    fontSize: "18px",
    marginTop: "5%",
  },
  available: {
    color: "darkgreen",
  },
  unavailable: {
    color: "darkred",
  },
  refreshbutton: {
    flex: 2,
    marginTop: "5%",
  },
});
