import React, { useEffect, useState } from "react";
import "react-native-gesture-handler";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { render } from "react-dom";
import { login } from "./API/mock";
import { setToken } from "./API/token";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  TextInput,
  Keyboard,
  TouchableWithoutFeedback,
} from "react-native";

// APP

export default function App() {
  // Deklaracja zmiennych

  const serwerAdress = "http://192.168.0.27:3000"; // baza danych
  const [allUsers, setAllUsers] = useState([]); // lista zarejestrowanych użytkowników
  const [Users, setUsers] = useState([]); // lista użytkowników
  const Stack = createStackNavigator(); // menu nawigacyjne

  // Widok listy użytkowników

  const ARMUsersList = ({ navigation }) => {
    return (
      <SafeAreaView style={styles.body}>
        <View style={styles.container}>
          <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
          <View style={styles.usersList}>
            <FlatList
              showsVerticalScrollIndicator={false}
              showsHorizontalScrollIndicator={false}
              data={Users}
              renderItem={({ item }) => (
                <View>
                  {item.State == 1 ? (
                    <Text style={styles.users}>
                      {item.Name} -{" "}
                      <Text style={states.unavailable}>Niedostępny</Text>
                    </Text>
                  ) : item.State == 2 ? (
                    <Text style={styles.users}>
                      {item.Name} - {""}
                      <Text style={states.available}>Dostępny</Text>
                    </Text>
                  ) : item.State == 3 ? (
                    <Text style={styles.users}>
                      {item.Name} - {""}
                      <Text style={states.busy}>Na akcji</Text>
                    </Text>
                  ) : (
                    <Text style={styles.users}>
                      Błąd wczytywania użytkowników
                    </Text>
                  )}
                </View>
              )}
            />
          </View>
          <View style={styles.refreshbutton}>
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={GetList}
            >
              <Text style={navigationStyle.navigationButtonText}>
                Wyświetl/Odśwież listę
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={() =>
                navigation.navigate("Mój profil", { name: "Mój profil" })
              }
            >
              <Text style={navigationStyle.navigationButtonText}>
                Mój profil
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={() =>
                navigation.navigate("Logowanie", { name: "Logowanie" })
              }
            >
              <Text style={navigationStyle.navigationButtonText}>Wyloguj</Text>
            </TouchableOpacity>
          </View>
        </View>
      </SafeAreaView>
    );
  };

  // Widok profilu

  const ARMMyProfile = ({ navigation, route }) => {
    return (
      <SafeAreaView style={styles.body}>
        <View>
          <Text style={styles.title}>Mój profil</Text>
          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={() => navigation.navigate("ARM", { name: "ARM" })}
          >
            <Text style={navigationStyle.navigationButtonText}>
              Powrót do listy
            </Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  };

  // Metoda wywołująca użytkoników z bazy

  const GetList = () => {
    fetch(serwerAdress + "/users")
      .then((response) => response.json())
      .then((json) => {
        setUsers(json);
      });
  };

  // Menu nawigacyjne

  return (
    <NavigationContainer style={navigationStyle.navigationMenu}>
      <Stack.Navigator>
        <Stack.Screen
          name="Logowanie"
          component={ARMLogin}
          options={({ title: "Logowanie" }, { headerShown: false })}
        />
        <Stack.Screen
          name="ARM"
          component={ARMUsersList}
          options={({ title: "ARM" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Mój profil"
          component={ARMMyProfile}
          options={({ title: "Mój profil" }, { headerShown: false })}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

// Widoki aplikacji

// Login

export function ARMLogin({ navigation, route }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errMessage, setErrMessage] = useState(""); // odpowiedź z API

  const loginUser = () => {
    setErrMessage("");
    login(username, password, true)
      .then(async (res) => {
        await setToken(res.auth_token);
        navigation.navigate("ARM", { name: "ARM" });
      })
      .catch((err) => setErrMessage(err.message));
  };

  return (
    <SafeAreaView style={styles.body}>
      <View>
        <Text style={styles.title}>Ekran logowania</Text>
        <TextInput
          style={styles.TextInputStyle}
          placeholder="Nazwa użytkownika"
          value={username}
          onChangeText={(val) => setUsername(val)}
        />
        <TextInput
          style={styles.TextInputStyle}
          placeholder="Hasło"
          value={password}
          onChangeText={(val) => setPassword(val)}
        />
        <TouchableOpacity
          style={navigationStyle.navigationButton}
          onPress={loginUser}
        >
          <Text style={navigationStyle.navigationButtonText}>Zaloguj</Text>
        </TouchableOpacity>
        {errMessage ? <Text>{errMessage}</Text> : null}
      </View>
    </SafeAreaView>
  );
}

