import { StatusBar } from "expo-status-bar";
import React from "react";
import { StyleSheet, Text, View, SafeAreaView, FlatList } from "react-native";

// APP

export default function App() {
  return (
    <SafeAreaView style={styles.body}>
      <View style={styles.container}>
        <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
        <FlatListBasics></FlatListBasics>
        <Button title="Test" onPress={() => console.log("hello")} />
      </View>
    </SafeAreaView>
  );
}

const FlatListBasics = () => {
  return (
    <View style={styles.usersList}>
      <FlatList
        data={[
          { key: "Jan", isAvailable: true },
          { key: "Daniel", isAvailable: false },
          { key: "Dominik", isAvailable: true },
          { key: "Jerzy", isAvailable: false },
          { key: "Adam", isAvailable: true },
        ]}
        renderItem={({ item }) => (
          <View>
            {item.isAvailable == true ? (
              <Text style={styles.users}>
                {item.key} - <Text style={styles.available}>Dostępny</Text>
              </Text>
            ) : (
              <Text style={styles.users}>
                {item.key} - <Text style={styles.unavailable}>Niedostępny</Text>
              </Text>
            )}
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  body: {
    height: "100%",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "dodgerblue",
  },
  container: {
    alignItems: "center",
    height: "80%",
  },
  title: {
    fontSize: "20px",
    fontWeight: "bold",
    marginBottom: "5%",
    marginTop: "5%",
  },
  usersList: {
    marginTop: "5%",
    marginBottom: "2,5%",
  },
  users: {
    fontSize: "18px",
    marginTop: "5%",
  },
  available: {
    color: "darkgreen",
  },
  unavailable: {
    color: "darkred",
  },
});

