import React, { useEffect, useState, Component } from "react";
import { render } from "react-dom";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  Button,
} from "react-native";

// APP

//var Users = [];
var request = new XMLHttpRequest();

export default function App() {
  const [Users, setUsers] = useState([]);

  const test = () => {
    request.onreadystatechange = (e) => {
      if (request.readyState !== 4) {
        return;
      }

      if (request.status === 200) {
        var list = [];
        list = JSON.parse(request.responseText);
        list = JSON.stringify(list);
        setUsers(list);
        //console.log("success", request.responseText);
      } else {
        console.warn("error");
      }
    };

    request.open("GET", "http://192.168.0.27:3000/users", true);
    request.send();
    console.log(Users);
  };

  const GetUsers = () => {
    request.onreadystatechange = (e) => {
      if (request.readyState !== 4) {
        return;
      }

      if (request.status === 200) {
        var list = [];
        list = JSON.parse(request.responseText);
        list = JSON.stringify(list);
        setUsers(list);
        //console.log("success", request.responseText);
      } else {
        console.warn("error");
      }
    };

    request.open("GET", "http://192.168.0.27:3000/users", true);
    request.send();

    console.log(Users);
    setUsers(Users);
    // fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
    //   .then((response) => response.json())
    //   .then(function (users) {
    //     console.log(users);
    //     for (let i = 0; i < users.length; i++) {
    //       console.log(users[i]);
    //     }
    //   });
  };

  return (
    <SafeAreaView style={styles.body}>
      <View style={styles.container}>
        <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
        <View style={styles.users}>
          <FlatList
            data={[]}
            renderItem={({ item }) => (
              <View>
                {item.IsAvailable.data.indexOf(1) == 0 ? (
                  <Text style={styles.users}>
                    {item.Name} - <Text style={styles.available}>Dostępny</Text>
                  </Text>
                ) : item.IsAvailable.data.indexOf(0) == 0 ? (
                  <Text style={styles.users}>
                    {item.Name} - {""}
                    <Text style={styles.unavailable}>Niedostępny</Text>
                  </Text>
                ) : (
                  <Text style={styles.users}>
                    Błąd wczytywania użytkowników
                  </Text>
                )}
              </View>
            )}
          />
        </View>
        <Button title="Test" onPress={test} />
        <Button title="Get Users" onPress={GetUsers} />
      </View>
    </SafeAreaView>
  );
}

// fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
//   .then((response) => response.json())
//   .then((json) => {
//     return json;
//   });

const FlatListBasics = (Users) => {
  console.log(Users);
  return (
    <View style={styles.usersList}>
      <FlatList
        data={Users}
        renderItem={({ item }) => (
          <View>
            {item.IsAvailable.data.indexOf(1) == 0 ? (
              <Text style={styles.users}>
                {item.Name} - <Text style={styles.available}>Dostępny</Text>
              </Text>
            ) : item.IsAvailable.data.indexOf(0) == 0 ? (
              <Text style={styles.users}>
                {item.Name} - {""}
                <Text style={styles.unavailable}>Niedostępny</Text>
              </Text>
            ) : (
              <Text style={styles.users}>Błąd wczytywania użytkowników</Text>
            )}
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  body: {
    height: "100%",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "dodgerblue",
  },
  container: {
    alignItems: "center",
    height: "80%",
  },
  title: {
    fontSize: "20px",
    fontWeight: "bold",
    marginBottom: "5%",
    marginTop: "5%",
  },
  usersList: {
    marginTop: "5%",
    marginBottom: "2,5%",
  },
  users: {
    fontSize: "18px",
    marginTop: "5%",
  },
  available: {
    color: "darkgreen",
  },
  unavailable: {
    color: "darkred",
  },
});
