  const GetList = () => {
    fetch(serwerAdress + "/users")
      .then((response) => response.json())
      .then((json) => {
        setUsers(json);
      });
  };

  const GetLoggedUser = () => {
    getToken().then((res) => setLoggedUser(res));
  };

  const GetRoles = () => {
    getToken().then((res) => {
      fetch(serwerAdress + "/getRoles?email=" + '"' + res + '"')
        .then((response) => response.json())
        .then((json) => {
          setRoleToken(json[0].Name);
        });
    });
