    getToken().then((res) => {
      setLoggedUser(res);
      fetch(serwerAdress + "/getUserInfo?email=" + '"' + res + '"')
        .then((response) => response.json())
        .then((json) => {
          setUserName(json[0].Name);
          setUserEmail(json[0].Email);
          setCurrentState(json[0].State);
        });
