export function ARMChangePassword({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");

  const [userEmail, setUserEmail] = useState("");

  const [shouldShow, setShouldShow] = useState(true);
  const [errMessage, setErrMessage] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [oldPassword, setOldPassword] = useState("");

  const GetLoggedUser = () => {
    getToken().then((res) => {
      setLoggedUser(res);
      fetch(serwerAdress + "/getUserInfo?email=" + '"' + res + '"')
        .then((response) => response.json())
        .then((json) => {
          setUserEmail(json[0].Email);
        });
    });
  };

  const GetUserPassword = () => {
    setErrMessage("");

    fetch(
      serwerAdress +
        "/getPassword?email=" +
        '"' +
        userEmail +
        '"&password=' +
        '"' +
        oldPassword +
        '"'
    )
      .then((response) => response.json())
      .then((json) => {
        if (json[0].result == 0) {
          setErrMessage("Podano złe hasło");
          setOldPassword("");
        }

        if (json[0].result == 1) {
          setShouldShow(!shouldShow);
        }
      });
  };

  const SetUserPassword = () => {
    setErrMessage("");

    fetch(
      serwerAdress +
        "/setPassword?email=" +
        '"' +
        userEmail +
        '"&password=' +
        '"' +
        newPassword +
        '"'
    )
      .then((response) => response.json())
      .then((json) => {
        if (json[0].result == 0) {
          setErrMessage("Coś poszło nie tak, spróbuj ponownie");
        }

        if (json[0].result == 1) {
          setShouldShow(!shouldShow);
          setNewPassword("");
          setOldPassword("");
          setErrMessage("Udało się zmienić hasło");
        }
      });
  };

  useEffect(() => {
    GetLoggedUser();
  }, []);

  return (
    <SafeAreaView style={styles.body} onTouchStart={Keyboard.dismiss}>
      <Text style={styles.loggedUserStyle}>
        Zalogowany jako, {loggedUser ? loggedUser : null}
      </Text>
      <View>
        <Text style={styles.title}>Zmiana hasła</Text>
        <View>
          {shouldShow ? (
            <View>
              <TextInput
                secureTextEntry={true}
                style={myProfile.newPasswordText}
                placeholder="Podaj stare hasło"
                value={oldPassword}
                onChangeText={(val) => setOldPassword(val)}
              />
              <TouchableOpacity
                style={navigationStyle.navigationButton}
                onPress={GetUserPassword}
              >
                <Text style={navigationStyle.navigationButtonText}>
                  Zmień hasło
                </Text>
              </TouchableOpacity>
            </View>
          ) : (
            <View>
              <TextInput
                secureTextEntry={true}
                style={myProfile.newPasswordText}
                placeholder="Podaj nowe hasło"
                value={newPassword}
                onChangeText={(val) => setNewPassword(val)}
              />
              <TouchableOpacity
                style={navigationStyle.navigationButton}
                onPress={SetUserPassword}
              >
                <Text style={navigationStyle.navigationButtonText}>
                  Zmień hasło
                </Text>
              </TouchableOpacity>
            </View>
          )}
          {errMessage ? (
            <View style={styles.errMessageStyle}>
              <Text style={styles.errMessageColor}>{errMessage}</Text>
            </View>
          ) : null}
          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={() =>
              navigation.navigate("Mój profil", { name: "Mój profil" })
            }
          >
            <Text style={navigationStyle.navigationButtonText}>
              Powrót do mojego profilu
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
}

export function ARMUserRegistration({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");

  const [shouldShow, setShouldShow] = useState(true);
  const [shouldDisable, setShouldDisable] = useState(true);
  const [errMessage, setErrMessage] = useState("");
  const [emailErrMessage, setEmailErrMessage] = useState("");

  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [role, setRole] = useState(1);

  const GetLoggedUser = () => {
    getToken().then((res) => {
      setLoggedUser(res);
    });
  };

  const validateEmail = (email) => {
    setEmailErrMessage("");
    setEmail(email);
    var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    var result = re.test(email);
    if (result == false) {
      setEmailErrMessage("Wpisano niepoprawny email");
      setShouldDisable(true);
    }

    if (result == true) {
      setEmailErrMessage("");
      setErrMessage("");
      setShouldDisable(false);
    }
  };

  const IsThereUser = () => {
    fetch(serwerAdress + "/isThereUser?email=" + '"' + email + '"')
      .then((response) => response.json())
      .then((json) => {
        if (json[0].result == 1) setErrMessage("Podany email już istnieje");

        if (json[0].result == 0) {
          setErrMessage("");
          setShouldShow(!shouldShow);
          sendRegistrationEmail();
        }
      });
  };

  const CreateUser = () => {
    fetch(
      serwerAdress +
        "/createUser?email=" +
        '"' +
        email +
        '"' +
        "&password=" +
        '"' +
        password +
        '"' +
        "&name=" +
        '"' +
        name +
        '"' +
        "&role=" +
        '"' +
        role +
        '"'
    )
      .then((response) => response.json())
      .then((json) => {
        if (json[0].result == 0)
          setErrMessage(
            "Wystąpił błąd w trakcie tworzenia użytkownika, spróbuj ponownie"
          );

        if (json[0].result == 1)
          setErrMessage("Udało się utworzyć nowego użytkownika");
      });
  };

  const sendRegistrationEmail = () => {
    RNSmtpMailer.sendMail({
      mailhost: "smtp.gmail.com",
      port: "465",
      ssl: true, // optional. if false, then TLS is enabled. Its true by default in android. In iOS TLS/SSL is determined automatically, and this field doesn't affect anything
      username: "usernameEmail",
      password: "password",
      fromName: "Some Name", // optional
      replyTo: "usernameEmail", // optional
      recipients: "toEmail1,toEmail2",
      bcc: ["bccEmail1", "bccEmail2"], // optional
      subject: "subject",
      htmlBody: "<h1>header</h1><p>body</p>",
      attachmentPaths: [""], // optional
      attachmentNames: [""], // required in android, these are renames of original files. in ios filenames will be same as specified in path. In a ios-only application, no need to define it
    })
      .then((success) => console.log(success))
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    GetLoggedUser();
  }, []);

  return (
    <SafeAreaView style={styles.body} onTouchStart={Keyboard.dismiss}>
      <Text style={styles.loggedUserStyle}>
        Zalogowany jako, {loggedUser ? loggedUser : null}
      </Text>
      <View>
        <Text style={styles.title}>Rejestracja nowego użytkownika</Text>
        <View>
          {shouldShow ? (
            <View>
              <TextInput
                style={myProfile.registrationText}
                placeholder="Podaj email"
                value={email}
                onChangeText={(val) => validateEmail(val)}
              />
              {emailErrMessage ? (
                <View style={styles.errMessageStyle}>
                  <Text style={styles.errMessageColor}>{emailErrMessage}</Text>
                </View>
              ) : null}
              <TouchableOpacity
                style={navigationStyle.navigationButton}
                disabled={shouldDisable}
                onPress={IsThereUser}
              >
                <Text style={navigationStyle.navigationButtonText}>
                  Zarejestruj
                </Text>
              </TouchableOpacity>
            </View>
          ) : (
            <View>
              <TextInput
                style={myProfile.registrationText}
                placeholder="Podaj email"
                value={email}
                editable={shouldShow}
                onChangeText={(val) => setEmail(val)}
              />
              <TextInput
                style={myProfile.registrationText}
                placeholder="Podaj Imię użytkownika"
                value={name}
                onChangeText={(val) => setName(val)}
              />
              <TextInput
                secureTextEntry={true}
                style={myProfile.registrationText}
                placeholder="Podaj tymczasowe hasło"
                value={password}
                onChangeText={(val) => setPassword(val)}
              />
              <Picker
                selectedValue={role}
                style={myProfile.statePicker}
                onValueChange={(itemValue, itemIndex) => setRole(itemValue)}
              >
                <Picker.Item label="Admin" value="1" />
                <Picker.Item label="Koordynator" value="2" />
                <Picker.Item label="Użytkownik" value="3" />
              </Picker>
              <TouchableOpacity
                style={navigationStyle.navigationButton}
                onPress={CreateUser}
              >
                <Text style={navigationStyle.navigationButtonText}>
                  Zarejestruj
                </Text>
              </TouchableOpacity>
            </View>
          )}
          {errMessage ? (
            <View style={styles.errMessageStyle}>
              <Text style={styles.errMessageColor}>{errMessage}</Text>
            </View>
          ) : null}
          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={() =>
              navigation.navigate("Mój profil", { name: "Mój profil" })
            }
          >
            <Text style={navigationStyle.navigationButtonText}>
              Powrót do mojego profilu
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
}

