import React, { useEffect, useState } from "react";
import jQuery from "jQuery";
import { render } from "react-dom";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  Button,
} from "react-native";

// APP

const wait = (timeout) => {
  return new Promise((resolve) => setTimeout(resolve, timeout));
};

export default function App() {
  const [refreshing, setRefreshing] = React.useState(false);

  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    wait(2000).then(() => setRefreshing(false));
  }, []);

  return (
    <SafeAreaView style={styles.body}>
      <ScrollView
        contentContainerStyle={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      ></ScrollView>
      <View style={styles.container}>
        <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
        <FlatListBasics></FlatListBasics>
        <Button title="Test" onPress={test} />
      </View>
    </SafeAreaView>
  );
}

var Users = [];
var request = new XMLHttpRequest();

request.onreadystatechange = (e) => {
  if (request.readyState !== 4) {
    return;
  }

  if (request.status === 200) {
    Users = JSON.parse(request.responseText);
    Users = JSON.stringify(Users);
    //console.log("success", request.responseText);
  } else {
    console.warn("error");
  }
};

request.open("GET", "http://192.168.0.27:3000/users", true);
request.send();

// fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
//   .then((response) => response.json())
//   .then((json) => {
//     return json;
//   });

const FlatListBasics = (Users) => {
  console.log(Users);
  return (
    <View style={styles.usersList}>
      <FlatList
        data={Users}
        renderItem={({ item }) => (
          <View>
            {item.IsAvailable.data.indexOf(1) == 0 ? (
              <Text style={styles.users}>
                {item.Name} - <Text style={styles.available}>Dostępny</Text>
              </Text>
            ) : item.IsAvailable.data.indexOf(0) == 0 ? (
              <Text style={styles.users}>
                {item.Name} - {""}
                <Text style={styles.unavailable}>Niedostępny</Text>
              </Text>
            ) : (
              <Text style={styles.users}>Błąd wczytywania użytkowników</Text>
            )}
          </View>
        )}
      />
    </View>
  );
};

function test() {
  console.log(Users);
  // fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
  //   .then((response) => response.json())
  //   .then(function (users) {
  //     console.log(users);
  //     for (let i = 0; i < users.length; i++) {
  //       console.log(users[i]);
  //     }
  //   });
}

const styles = StyleSheet.create({
  body: {
    height: "100%",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "dodgerblue",
  },
  container: {
    alignItems: "center",
    height: "80%",
  },
  title: {
    fontSize: "20px",
    fontWeight: "bold",
    marginBottom: "5%",
    marginTop: "5%",
  },
  usersList: {
    marginTop: "5%",
    marginBottom: "2,5%",
  },
  users: {
    fontSize: "18px",
    marginTop: "5%",
  },
  available: {
    color: "darkgreen",
  },
  unavailable: {
    color: "darkred",
  },
});
