import React, { useEffect, useState } from "react";
import "react-native-gesture-handler";
import { NavigationContainer, useFocusEffect } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { login } from "./API/mock";
import Moment from "moment";
import "moment/min/locales";
import "intl";
import "intl/locale-data/jsonp/pl";
import { vw, vh } from "react-native-viewport-units-fix";
import {
  getToken,
  setToken,
  getRoleToken,
  setRoleToken,
  getEmailToken,
  setEmailToken,
  getEventToken,
  setEventToken,
} from "./API/token";
import { DateTimePickerModal } from "react-native-paper-datetimepicker";
import { Picker } from "@react-native-picker/picker";
import DropDownPicker from "react-native-dropdown-picker";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  TextInput,
  Keyboard,
  TouchableWithoutFeedback,
  Platform,
} from "react-native";
import ARMLogin from "./Views/ARMLogin";
import ARMUsersList from "./Views/ARMUsersList";
import ARMMyProfile from "./Views/ARMMyProfile";
import ARMChangePassword from "./Views/ARMChangePassword";

// Deklaracja zmiennych globalnych

const serwerAdress = "https://arm-dev.herokuapp.com"; // API

const dismissKeyboard = () => {
  if (Platform.OS != "web") {
    Keyboard.dismiss();
  }
};

// APP

export default function App() {
  // Deklaracja zmiennych

  const Stack = createStackNavigator();

  // Menu nawigacyjne

  return (
    <NavigationContainer style={navigationStyle.navigationMenu}>
      <Stack.Navigator>
        <Stack.Screen
          name="Logowanie"
          component={ARMLogin}
          options={({ title: "Logowanie" }, { headerShown: false })}
        />
        <Stack.Screen
          name="ARM"
          component={ARMUsersList}
          options={({ title: "ARM" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Mój profil"
          component={ARMMyProfile}
          options={({ title: "Mój profil" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Zmiana hasła"
          component={ARMChangePassword}
          options={({ title: "Zmiana hasła" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Rejestracja"
          component={ARMUserRegistration}
          options={({ title: "Rejestracja" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Informacje o Użytkowniku"
          component={ARMUserDetails}
          options={({ title: "Informacje" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Wydarzenia"
          component={ARMEvents}
          options={({ title: "Wydarzenia" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Informacje o Wydarzeniu"
          component={ARMEventDetails}
          options={
            ({ title: "Informacje o Wydarzeniu" }, { headerShown: false })
          }
        />
        <Stack.Screen
          name="Dodaj wydarzenie"
          component={ARMAddEvent}
          options={({ title: "Dodaj wydarzenie" }, { headerShown: false })}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

// Widoki aplikacji

// Login

// Lista użytkowników

// Mój profil

export function ARMUserRegistration({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");

  const [shouldShow, setShouldShow] = useState(true);
  const [shouldDisable, setShouldDisable] = useState(true);
  const [errMessage, setErrMessage] = useState("");
  const [emailErrMessage, setEmailErrMessage] = useState("");

  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [role, setRole] = useState(1);

  const GetLoggedUser = () => {
    getToken().then((res) => {
      setLoggedUser(res);
    });
  };

  const validateEmail = (email) => {
    setEmailErrMessage("");
    setEmail(email);
    var re =
      /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    var result = re.test(email);
    if (result == false) {
      setEmailErrMessage("Wpisano niepoprawny email");
      setShouldDisable(true);
    }

    if (result == true) {
      setEmailErrMessage("");
      setErrMessage("");
      setShouldDisable(false);
    }
  };

  const IsThereUser = () => {
    fetch(serwerAdress + "/isThereUser/" + "'" + email + "'")
      .then((response) => response.json())
      .then((json) => {
        if (json[0].result == 1) setErrMessage("Podany email już istnieje");

        if (json[0].result == 0) {
          setErrMessage("");
          setShouldShow(!shouldShow);
        }
      });
  };

  const CreateUser = () => {
    fetch(
      serwerAdress +
        "/createUser/" +
        "'" +
        email +
        "'" +
        "/" +
        "'" +
        name +
        "'" +
        "/" +
        "'" +
        password +
        "'" +
        "/" +
        "'" +
        role +
        "'"
    )
      .then((response) => response.json())
      .then((json) => {
        if (json[0].result == 0)
          setErrMessage(
            "Wystąpił błąd w trakcie tworzenia użytkownika, spróbuj ponownie"
          );

        if (json[0].result == 1) {
          setErrMessage("Udało się utworzyć nowego użytkownika");
          sendRegistrationEmail();
        }
      });
  };

  const sendRegistrationEmail = () => {
    fetch(serwerAdress + "/sendMail/" + password + "/" + email);
  };

  useEffect(() => {
    GetLoggedUser();
  }, []);

  return (
    <TouchableWithoutFeedback onPress={() => dismissKeyboard()}>
      <SafeAreaView style={styles.body}>
        <Text style={styles.loggedUserStyle}>
          Zalogowany jako, {loggedUser ? loggedUser : null}
        </Text>
        <View>
          <Text style={styles.title}>Rejestracja nowego użytkownika</Text>
          <View>
            {shouldShow ? (
              <View>
                <TextInput
                  style={myProfile.registrationText}
                  placeholder="Podaj email"
                  value={email}
                  onChangeText={(val) => validateEmail(val)}
                />
                {emailErrMessage ? (
                  <View style={styles.errMessageStyle}>
                    <Text style={styles.errMessageColor}>
                      {emailErrMessage}
                    </Text>
                  </View>
                ) : null}
                <TouchableOpacity
                  style={navigationStyle.navigationButton}
                  disabled={shouldDisable}
                  onPress={IsThereUser}
                >
                  <Text style={navigationStyle.navigationButtonText}>
                    Zarejestruj
                  </Text>
                </TouchableOpacity>
              </View>
            ) : (
              <View>
                <TextInput
                  style={myProfile.registrationText}
                  placeholder="Podaj email"
                  value={email}
                  editable={shouldShow}
                  onChangeText={(val) => setEmail(val)}
                />
                <TextInput
                  style={myProfile.registrationText}
                  placeholder="Podaj Imię użytkownika"
                  value={name}
                  onChangeText={(val) => setName(val)}
                />
                <TextInput
                  secureTextEntry={true}
                  style={myProfile.registrationText}
                  placeholder="Podaj tymczasowe hasło"
                  value={password}
                  onChangeText={(val) => setPassword(val)}
                />
                <Picker
                  selectedValue={role}
                  style={myProfile.statePicker}
                  onValueChange={(itemValue, itemIndex) => setRole(itemValue)}
                >
                  <Picker.Item label="Admin" value="1" />
                  <Picker.Item label="Koordynator" value="2" />
                  <Picker.Item label="Użytkownik" value="3" />
                </Picker>
                <TouchableOpacity
                  style={navigationStyle.navigationButton}
                  onPress={CreateUser}
                >
                  <Text style={navigationStyle.navigationButtonText}>
                    Zarejestruj
                  </Text>
                </TouchableOpacity>
              </View>
            )}
            {errMessage ? (
              <View style={styles.errMessageStyle}>
                <Text style={styles.errMessageColor}>{errMessage}</Text>
              </View>
            ) : null}
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={() =>
                navigation.navigate("Mój profil", { name: "Mój profil" })
              }
            >
              <Text style={navigationStyle.navigationButtonText}>
                Powrót do mojego profilu
              </Text>
            </TouchableOpacity>
          </View>
        </View>
      </SafeAreaView>
    </TouchableWithoutFeedback>
  );
}

export function ARMUserDetails({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");

  const [userName, setUserName] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userRole, setUserRole] = useState("");
  const [currentState, setCurrentState] = useState("");

  const GetLoggedUser = () => {
    getToken().then((res) => {
      setLoggedUser(res);
    });
  };

  const GetSelectedUserInfo = () => {
    getEmailToken().then((res) => {
      fetch(serwerAdress + "/getSelectedUserInfo/" + "'" + res + "'")
        .then((response) => response.json())
        .then((json) => {
          setUserName(json.Name ? json.Name : json[0].Name);
          setUserEmail(json.Email ? json.Email : json[0].Email);
          setCurrentState(json.State ? json.State : json[0].State);
          setUserRole(json.RoleName ? json.RoleName : json[0].RoleName);
        });
    });
  };

  useEffect(() => {
    GetLoggedUser();
    GetSelectedUserInfo();
  }, []);

  return (
    <SafeAreaView style={styles.body}>
      <Text style={styles.loggedUserStyle}>
        Zalogowany jako, {loggedUser ? loggedUser : null}
      </Text>
      <View>
        <Text style={styles.title}>Informacje o użytkowniku</Text>
        <View>
          <Text style={myProfile.myData}>
            Imię: {userName ? userName : null}
          </Text>
          <Text style={myProfile.myData}>
            Email: {userEmail ? userEmail : null}
          </Text>
          <Text style={myProfile.myData}>
            Rola: {userRole ? userRole : null}
          </Text>
          <Text style={myProfile.myData}>
            Status:{" "}
            {currentState == 1 ? (
              <Text style={states.available}>Dostępny</Text>
            ) : currentState == 2 ? (
              <Text style={states.unavailable}>Niedostępny</Text>
            ) : currentState == 3 ? (
              <Text style={states.busy}>Na akcji</Text>
            ) : null}
          </Text>

          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={() => navigation.goBack()}
          >
            <Text style={navigationStyle.navigationButtonText}>Powrót</Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
}

// Wydarzenia

export function ARMEvents({ navigation }) {
  const [Events, setEvents] = useState([]); // lista wydarzeń
  const [loggedUser, setLoggedUser] = useState("");
  const [userRole, setUserRole] = useState("");

  const GetEvents = () => {
    fetch(serwerAdress + "/events")
      .then((response) => response.json())
      .then((json) => {
        setEvents(json);
      });
  };

  const GetLoggedUser = () => {
    getToken().then((res) => setLoggedUser(res));
  };

  const GetUserRole = () => {
    getRoleToken().then((res) => setUserRole(res));
  };

  const GetEventDetails = (Id) => {
    setEventToken(Id);
    navigation.navigate("Informacje o Wydarzeniu", {
      name: "Informacja o Wydarzeniu",
    });
  };

  useFocusEffect(
    React.useCallback(() => {
      GetLoggedUser();
      GetEvents();
      Moment.locale("pl");
      GetUserRole();
    }, [])
  );

  return (
    <SafeAreaView style={styles.body}>
      <Text style={styles.loggedUserStyle}>
        Zalogowany jako, {loggedUser ? loggedUser : null}
      </Text>
      <View style={styles.container}>
        <Text style={styles.title}>Lista wydarzeń</Text>
        <View style={styles.eventsList}>
          {Events.length != 0 ? (
            <FlatList
              showsVerticalScrollIndicator={false}
              showsHorizontalScrollIndicator={false}
              data={Events}
              renderItem={({ item }) => (
                <View style={styles.marginTop15}>
                  <TouchableOpacity>
                    <Text
                      style={styles.events}
                      onPress={() => GetEventDetails(item.Id)}
                    >
                      {item.Name}
                      {"\n"}
                      {Moment(item.DateFrom).format("DD-MM-yyyy HH:mm")}
                      {"\n"}
                      {item.DateTo == null || item.DateTo == ""
                        ? "-"
                        : Moment(item.DateTo).format("DD-MM-yyyy HH:mm")}
                    </Text>
                  </TouchableOpacity>
                </View>
              )}
              keyExtractor={(item, index) => index.toString()}
            />
          ) : (
            <Text style={styles.events}>Brak wydarzeń</Text>
          )}
        </View>
        <View style={styles.refreshbutton}>
          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={GetEvents}
          >
            <Text style={navigationStyle.navigationButtonText}>
              Odśwież listę wydarzeń
            </Text>
          </TouchableOpacity>
          {userRole == "Admin" || userRole == "Koordynator" ? (
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={() =>
                navigation.navigate("Dodaj wydarzenie", {
                  name: "Dodaj wydarzenie",
                })
              }
            >
              <Text style={navigationStyle.navigationButtonText}>
                Dodaj wydarzenie
              </Text>
            </TouchableOpacity>
          ) : null}
          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={() => navigation.navigate("ARM", { name: "ARM" })}
          >
            <Text style={navigationStyle.navigationButtonText}>
              Powrót do listy użytkowników
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
}

export function ARMEventDetails({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");
  const [userRole, setUserRole] = useState("");
  const [eventId, setEventId] = useState(0);
  const [errMessage, setErrMessage] = useState("");

  const [UsersInEvents, setUsersInEvents] = useState([]);

  const [shouldShow, setShouldShow] = useState(true);

  const [eventName, setEventName] = useState("");
  const [eventLocalization, setEventLokalization] = useState("");
  const [eventFrom, setEventFrom] = useState("");
  const [eventTo, setEventTo] = useState("");

  const GetLoggedUser = () => {
    getToken().then((res) => {
      setLoggedUser(res);
    });
  };

  const GetUserRole = () => {
    getRoleToken().then((res) => {
      setUserRole(res);
    });
  };

  const GetUserDetails = (email) => {
    setEmailToken(email);
    navigation.navigate("Informacje o Użytkowniku", { name: "Informacje" });
  };

  const GetEventInfo = () => {
    getEventToken().then((res) =>
      fetch(serwerAdress + "/getEventById/" + res)
        .then((response) => response.json())
        .then((json) => {
          setEventId(json.Id);
          setEventName(json.Name);
          setEventLokalization(json.Localization);
          setEventFrom(json.DateFrom);
          setEventTo(json.DateTo);
        })
    );
  };

  const GetUsersInEvent = () => {
    getEventToken().then((res) => {
      fetch(serwerAdress + "/getUsersInEvents/" + res)
        .then((res) => res.json())
        .then((json) => {
          if (json.result == 0) {
            setUsersInEvents([]);
            return;
          }
          setUsersInEvents(json);
          getToken().then((res) => {
            for (var i = 0; i < json.length; i++) {
              if (json[i].Email == res) {
                setShouldShow(!shouldShow);
                return;
              }
            }
          });
        });
    });
  };

  const AddToEvent = () => {
    fetch(serwerAdress + "/getUserInfo/" + "'" + loggedUser + "'")
      .then((res) => res.json())
      .then((json) => {
        fetch(serwerAdress + "/addToEvent/" + json[0].Id + "/" + eventId)
          .then((json) => json.json())
          .then((response) => {
            if (response[0].result == 0)
              setErrMessage(
                "Wystąpił błąd w trakcie dołączania do wydarzenia, spróbuj ponownie"
              );
            if (response[0].result == 1) {
              setErrMessage("Udało się dołączyć do wydarzenia");
              GetUsersInEvent();
              setShouldShow(!shouldShow);
            }
          });
      });
  };

  const QuitEvent = () => {
    fetch(serwerAdress + "/getUserInEvent/" + "'" + loggedUser + "'")
      .then((res) => res.json())
      .then((json) =>
        fetch(
          serwerAdress + "/deleteFromEvent/" + json[0].Id + "/" + eventId
        ).then((response) =>
          response.json().then((json) => {
            if (json[0].result == 0)
              setErrMessage(
                "Wystąpił błąd w trakcie opuszczania wydarzenia, spróbuj ponownie"
              );
            if (json[0].result == 1) {
              setErrMessage("Udało się opuścić wydarzenie");
              GetUsersInEvent();
              setShouldShow(!shouldShow);
            }
          })
        )
      );
  };

  const DeleteEvent = () => {
    fetch(serwerAdress + "/deleteEvent/" + eventId).then(() => {
      navigation.navigate("Wydarzenia", { name: "Wydarzenia" });
    });
  };

  useEffect(() => {
    GetLoggedUser();
    GetEventInfo();
    GetUsersInEvent();
    GetUserRole();
  }, []);

  return (
    <SafeAreaView style={styles.body}>
      <Text style={styles.loggedUserStyle}>
        Zalogowany jako, {loggedUser ? loggedUser : null}
      </Text>
      <View>
        <Text style={styles.title}>Informacje o wydarzeniu</Text>
        <View>
          <Text style={myProfile.myData}>
            Nazwa: {eventName ? eventName : null}
          </Text>
          <Text style={myProfile.myData}>
            Lokalizacja: {eventLocalization ? eventLocalization : null}
          </Text>
          <Text style={myProfile.myData}>
            Data rozpoczęcia:{" "}
            {eventFrom ? Moment(eventFrom).format("DD-MM-yyyy HH:mm") : null}
          </Text>
          <Text style={myProfile.myData}>
            Data zakończenia:{" "}
            {eventTo ? Moment(eventTo).format("DD-MM-yyyy HH:mm") : null}
          </Text>
          <Text style={styles.title}>Osoby biorące udział w wydarzeniu: </Text>
          <View style={styles.usersInEventList}>
            {UsersInEvents.length != 0 ? (
              <FlatList
                showsVerticalScrollIndicator={false}
                showsHorizontalScrollIndicator={false}
                data={UsersInEvents}
                renderItem={({ item }) => (
                  <TouchableOpacity style={styles.marginTop15}>
                    <Text
                      style={styles.events}
                      onPress={() => GetUserDetails(item.Email)}
                    >
                      {item.UserName} - {item.Email}
                    </Text>
                  </TouchableOpacity>
                )}
                keyExtractor={(item, index) => index.toString()}
              />
            ) : (
              <Text style={styles.events}>
                Brak użytkowników biorących udział
              </Text>
            )}
          </View>
          {shouldShow ? (
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={AddToEvent}
            >
              <Text style={navigationStyle.navigationButtonText}>
                Dołącz do wydarzenia
              </Text>
            </TouchableOpacity>
          ) : (
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={QuitEvent}
            >
              <Text style={navigationStyle.navigationButtonText}>
                Opuść wydarzenie
              </Text>
            </TouchableOpacity>
          )}
          {errMessage ? (
            <View style={styles.eventErrMessageStyle}>
              <Text style={styles.errMessageColor}>{errMessage}</Text>
            </View>
          ) : null}
          {userRole == "Admin" || userRole == "Koordynator" ? (
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={DeleteEvent}
            >
              <Text style={navigationStyle.navigationButtonText}>
                Usuń wydarzenie
              </Text>
            </TouchableOpacity>
          ) : null}
          <TouchableOpacity
            style={navigationStyle.navigationButton}
            onPress={() =>
              navigation.navigate("Wydarzenia", { name: "Wydarzenia" })
            }
          >
            <Text style={navigationStyle.navigationButtonText}>
              Powrót do wydarzeń
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
}

export function ARMAddEvent({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");

  const [errEventName, setErrEventName] = useState("");
  const [errEventLocalization, setErrEventLocalization] = useState("");
  const [errMessage, setErrMessage] = useState("");
  const [errFromMessage, setErrFromMessage] = useState("");
  const [errToMessage, setErrToMessage] = useState("");

  const [eventName, setEventName] = useState("");
  const [eventLocalization, setEventLocalization] = useState("");
  const [eventFrom, setEventFrom] = useState(new Date());
  const [eventTo, setEventTo] = useState(new Date());

  const [visibleFrom, setVisibleFrom] = useState(false);
  const [visibleTo, setVisibleTo] = useState(false);

  const GetLoggedUser = () => {
    getToken().then((res) => {
      setLoggedUser(res);
    });
  };

  const eventNameInput = (val) => {
    setErrEventName("");
    if (val == "") {
      setErrEventName("Uzupełnij nazwę wydarzenia");
    }
    setEventName(val);
  };

  const eventLocalizationInput = (val) => {
    setErrEventLocalization("");
    if (val == "") {
      setErrEventLocalization("Uzupełnij localizację wydarzenia");
    }
    setEventLocalization(val);
  };

  const onDismissFrom = () => {
    setVisibleFrom(false);
  };

  const onDismissTo = () => {
    setVisibleTo(false);
  };

  const onChangeFrom = (date) => {
    setVisibleFrom(false);
    setEventFrom(date);
  };

  const onChangeTo = (date) => {
    setVisibleTo(false);
    setEventTo(date);
  };

  // const validateFromDate = (date) => {
  //   setErrFromMessage("");
  //   setErrMessage("");
  //   setEventFrom(date);
  //   if (date == null || date == "") return;
  //   var re =
  //     /^([1-9]|([012][0-9])|(3[01]))-([0]{0,1}[1-9]|1[012])-\d\d\d\d\s([0-1]?[0-9]|2?[0-3]):([0-5]\d)$/;
  //   var result = re.test(date);
  //   if (result == false) {
  //     setErrFromMessage(
  //       "Wpisano niepoprawną datę rozpoczęcia \nData powinna mieć format dd-mm-yyyy hh:mm"
  //     );
  //   }

  //   if (result == true) {
  //     setErrMessage("");
  //     setErrFromMessage("");
  //   }
  // };

  // const validateToDate = (date) => {
  //   setErrToMessage("");
  //   setErrMessage("");
  //   setEventTo(date);
  //   if (date == null || date == "") return;
  //   var re =
  //     /^([1-9]|([012][0-9])|(3[01]))-([0]{0,1}[1-9]|1[012])-\d\d\d\d\s([0-1]?[0-9]|2?[0-3]):([0-5]\d)$/;
  //   var result = re.test(date);
  //   if (result == false) {
  //     setErrToMessage(
  //       "Wpisano niepoprawną datę zakończenia \nData powinna mieć format dd-mm-yyyy hh:mm"
  //     );
  //   }

  //   if (result == true) {
  //     setErrMessage("");
  //     setErrToMessage("");
  //   }
  // };

  const AddEvent = () => {
    setErrMessage("");
    setErrEventName("");
    setErrEventLocalization("");

    if (eventName == "") {
      setErrEventName("Uzupełnij nazwę wydarzenia");
    }
    if (eventLocalization == "") {
      setErrEventLocalization("Uzupełnij lokalizację wydarzenia");
    }
    if (eventFrom == "") {
      setErrFromMessage("Uzupełnij datę rozpoczęcia");
    }
    if (eventTo != null || eventTo != "") {
      if (new Date(eventFrom) > new Date(eventTo)) {
        setErrMessage(
          "Data rozpoczęcia nie może być wcześniejsza od daty zakończenia"
        );
        return;
      }
    }

    if (eventName == "" || eventLocalization == "" || eventFrom == "") return;

    var dateFrom = Moment(eventFrom, "DD-MM-yyyy HH:mm").format(
      "yyyy-MM-DD HH:mm"
    );
    var dateTo = null;
    if (eventTo != "")
      dateTo =
        "'" +
        Moment(eventTo, "DD-MM-yyyy HH:mm").format("yyyy-MM-DD HH:mm") +
        "'";

    fetch(
      serwerAdress +
        "/addEvent/" +
        "'" +
        eventName +
        "'" +
        "/" +
        "'" +
        eventLocalization +
        "'" +
        "/" +
        "'" +
        dateFrom +
        "'" +
        "/" +
        dateTo
    )
      .then((res) => res.json())
      .then((json) => {
        if (json[0].result == 1) {
          setErrMessage("Udało się stworzyć nowe wydarzenie");
          navigation.navigate("Wydarzenia", { name: "Wydarzenia" });
        }

        if (json[0].result == 0)
          setErrMessage(
            "Nie udało się utworzyć nowego wydarzenia, spróbuj ponownie"
          );
      });
  };

  useEffect(() => {
    GetLoggedUser();
    Moment.locale("pl");
  }, []);

  return (
    <TouchableWithoutFeedback onPress={() => dismissKeyboard()}>
      <SafeAreaView style={styles.body}>
        <Text style={styles.loggedUserStyle}>
          Zalogowany jako, {loggedUser ? loggedUser : null}
        </Text>
        <View style={{ width: 300 }}>
          <Text style={styles.title}>Dodaj wydarzenie</Text>
          <View>
            <TextInput
              style={styles.textInputStyle}
              placeholder="Nazwa wydarzenia"
              value={eventName}
              onChangeText={(val) => eventNameInput(val)}
            />
            {errEventName ? (
              <View style={styles.eventErrMessageStyle}>
                <Text style={styles.errMessageColor}>{errEventName}</Text>
              </View>
            ) : null}
            <TextInput
              style={styles.textInputStyle}
              placeholder="Podaj lokalizację"
              value={eventLocalization}
              onChangeText={(val) => eventLocalizationInput(val)}
            />
            {errEventLocalization ? (
              <View style={styles.eventErrMessageStyle}>
                <Text style={styles.errMessageColor}>
                  {errEventLocalization}
                </Text>
              </View>
            ) : null}
            <DateTimePickerModal
              visible={visibleFrom}
              onDismiss={onDismissFrom}
              date={eventFrom}
              onConfirm={(val) => onChangeFrom(val)}
            />
            <TouchableOpacity onPress={() => setVisibleFrom(true)}>
              <Text>Wybierz datę rozpoczęcia</Text>
              <Text>{Moment(eventFrom).format("DD-MM-yyyy HH:mm")}</Text>
            </TouchableOpacity>
            {errFromMessage ? (
              <View style={styles.eventErrMessageStyle}>
                <Text style={styles.errMessageColor}>{errFromMessage}</Text>
              </View>
            ) : null}
            <DateTimePickerModal
              visible={visibleTo}
              onDismiss={onDismissTo}
              date={eventTo}
              onConfirm={(val) => onChangeTo(val)}
            />
            <TouchableOpacity onPress={() => setVisibleTo(true)}>
              <Text>Wybierz datę zakończenia</Text>
              <Text>{Moment(eventTo).format("DD-MM-yyyy HH:mm")}</Text>
            </TouchableOpacity>
            {errToMessage ? (
              <View style={styles.eventErrMessageStyle}>
                <Text style={styles.errMessageColor}>{errToMessage}</Text>
              </View>
            ) : null}
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={AddEvent}
            >
              <Text style={navigationStyle.navigationButtonText}>
                Dodaj wydarzenie
              </Text>
            </TouchableOpacity>
            {errMessage ? (
              <View style={styles.eventErrMessageStyle}>
                <Text style={styles.errMessageColor}>{errMessage}</Text>
              </View>
            ) : null}
            <TouchableOpacity
              style={navigationStyle.navigationButton}
              onPress={() =>
                navigation.navigate("Wydarzenia", { name: "Wydarzenia" })
              }
            >
              <Text style={navigationStyle.navigationButtonText}>
                Powrót do wydarzeń
              </Text>
            </TouchableOpacity>
          </View>
        </View>
      </SafeAreaView>
    </TouchableWithoutFeedback>
  );
}

