import React, { useEffect, useState } from "react";
import "react-native-gesture-handler";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { render } from "react-dom";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
} from "react-native";

// APP

export default function App() {
  // Deklaracja stanu użytkowników

  const [Users, setUsers] = useState([]);
  const Stack = createStackNavigator();

  // Widok logowania

  const ARMLogin = ({ navigation, route }) => {
    return (
      <SafeAreaView style={styles.body}>
        <View>
          <Text style={styles.title}>Ekran logowania</Text>
          <TouchableOpacity
            onPress={() => navigation.navigate("ARM", { name: "ARM" })}
          >
            <Text>Zaloguj</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  };

  // Widok listy użytkowników

  const ARMUsersList = ({ navigation }) => {
    return (
      <SafeAreaView style={styles.body}>
        <View style={styles.container}>
          <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
          <View style={styles.usersList}>
            <FlatList
              showsVerticalScrollIndicator={false}
              showsHorizontalScrollIndicator={false}
              data={Users}
              renderItem={({ item }) => (
                <View>
                  {item.State == 1 ? (
                    <Text style={styles.users}>
                      {item.Name} -{" "}
                      <Text style={states.unavailable}>Niedostępny</Text>
                    </Text>
                  ) : item.State == 2 ? (
                    <Text style={styles.users}>
                      {item.Name} - {""}
                      <Text style={states.available}>Dostępny</Text>
                    </Text>
                  ) : item.State == 3 ? (
                    <Text style={styles.users}>
                      {item.Name} - {""}
                      <Text style={states.busy}>Na akcji</Text>
                    </Text>
                  ) : (
                    <Text style={styles.users}>
                      Błąd wczytywania użytkowników
                    </Text>
                  )}
                </View>
              )}
            />
          </View>
          <View style={styles.refreshbutton}>
            <TouchableOpacity onPress={GetList} />
            <TouchableOpacity
              onPress={() =>
                navigation.navigate("Mój profil", { name: "Mój profil" })
              }
            />
            <TouchableOpacity
              onPress={() =>
                navigation.navigate("Logowanie", { name: "Logowanie" })
              }
            />
          </View>
        </View>
      </SafeAreaView>
    );
  };

  // Widok profilu

  const ARMMyProfile = ({ navigation, route }) => {
    return (
      <SafeAreaView style={styles.body}>
        <View>
          <Text style={styles.title}>Mój profil</Text>
          <Button
            title="Mój profil"
            color="black"
            style={navigation.navigationButton}
            onPress={() =>
              navigation.navigate("Mój profil", { name: "Mój profil" })
            }
          ></Button>
        </View>
      </SafeAreaView>
    );
  };

  // Metoda wywołująca użytkoników z bazy

  const GetList = () => {
    fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
      .then((response) => response.json())
      .then((json) => {
        setUsers(json);
      });
  };

  // Menu nawigacyjne

  return (
    <NavigationContainer style={navigation.navigationMenu}>
      <Stack.Navigator>
        <Stack.Screen
          name="Logowanie"
          component={ARMLogin}
          options={({ title: "Logowanie" }, { headerShown: false })}
        />
        <Stack.Screen
          name="ARM"
          component={ARMUsersList}
          options={({ title: "ARM" }, { headerShown: false })}
        />
        <Stack.Screen
          name="Mój profil"
          component={ARMMyProfile}
          options={({ title: "Mój profil" }, { headerShown: false })}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

// Style kaskadowe do listy użytkowników

const styles = StyleSheet.create({
  body: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "dodgerblue",
  },
  container: {
    height: "80%",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: "5%",
    marginTop: "5%",
  },
  usersList: {
    marginTop: "5%",
    marginBottom: 5,
    height: "68.9%",
    alignItems: "center",
  },
  users: {
    alignItems: "center",
    fontSize: 18,
    marginTop: 20,
  },
  refreshbutton: {
    marginTop: "5%",
  },
  button: {
    alignItems: "center",
    fontSize: 15,
    backgroundColor: "lightblue",
    padding: 10,
  },
});

const states = StyleSheet.create({
  available: {
    color: "darkgreen",
  },
  unavailable: {
    color: "darkred",
  },
  busy: {
    color: "orange",
  },
});

const navigation = StyleSheet.create({
  navigationMenu: {
    display: "none",
    alignContent: "flex-end",
    justifyContent: "center",
  },
  navigationButton: {
    color: "blue",
  },
});
