export function ARMMyProfile({ navigation }) {
  const [loggedUser, setLoggedUser] = useState("");
  const [userInfo, setUserInfo] = useState([]);

  const GetLoggedUser = () => {
    getToken().then((res) => setLoggedUser(res));
  };

  const GetUserInfo = () => {
    fetch(serwerAdress + "/userToLogin?email=" + '"' + loggedUser + '"')
      .then((response) => response.json())
      .then((json) => {
        setUserInfo(json);
      });
  };

  GetLoggedUser();
  GetUserInfo();

  return (
    <SafeAreaView style={styles.body}>
      <Text style={styles.loggedUserStyle}>
        Zalogowany jako, {loggedUser ? loggedUser : null}
      </Text>
      <View>
        <Text style={styles.title}>Mój profil</Text>
        <FlatList
          showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false}
          data={userInfo}
          renderItem={({ item }) => (
            <View>
              <Text>Imię: {item.name}</Text>
            </View>
          )}
