  eventErrMessageStyle: {
    padding: 10,
    position: "relative",
    width: "auto"
  },
  errMessageColor: {
    color: "orange"
  },
  loggedUserStyle: {
    position: "absolute",
    top: 20,
    left: 0,
    fontSize: 1.5 * vh
  },
  marginTop15: {
    marginTop: 15
  }
});
var states = StyleSheet.create({
  available: {
    color: "darkgreen"
  },
  unavailable: {
    color: "darkred"
  },
  busy: {
    color: "orange"
  }
});
var navigationStyle = StyleSheet.create({
  navigationMenu: {
    display: "none",
    alignContent: "flex-end",
    justifyContent: "center"
  },
  navigationButton: {
    alignItems: "center",
    justifyContent: "center",
    padding: 1 * vh,
    backgroundColor: "#0064C8",
    marginTop: 10,
    borderRadius: 5
  },
  loginButton: {
    alignItems: "center",
    justifyContent: "center",
    padding: 1 * vh,
    backgroundColor: "#0064C8",
    width: 200,
    borderRadius: 5
  },
  navigationButtonText: {
    alignItems: "center",
    fontSize: 2 * vh,
    color: "white"
  }
});
var myProfile = StyleSheet.create({
  myData: {
    fontSize: 2 * vh,
    padding: 2,
    marginTop: 5,
    fontWeight: "bold"
  },
