import React, { useEffect, useState } from "react";
import "react-native-gesture-handler";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { render } from "react-dom";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  Button,
} from "react-native";

// APP

export default function App() {
  // Deklaracja stanu użytkowników

  const [Users, setUsers] = useState([]);
  const Stack = createStackNavigator();

  const ARMLogin = ({ navigation, route }) => {
    return (
      <View>
        <Text>Ekran logowania</Text>
        <Button
          title="Mój profil"
          onPress={() =>
            navigation.navigate("Mój profil", { name: "Mój profil" })
          }
        />
      </View>
    );
  };
  const ARMUsersList = ({ navigation }) => {
    return (
      <SafeAreaView style={styles.body}>
        <View style={styles.container}>
          <Text style={styles.title}>Aplikacja Ratowników Mazowsza</Text>
          <View style={styles.usersList}>
            <FlatList
              showsVerticalScrollIndicator={false}
              showsHorizontalScrollIndicator={false}
              data={Users}
              renderItem={({ item }) => (
                <View>
                  {item.State == 1 ? (
                    <Text style={styles.users}>
                      {item.Name} -{" "}
                      <Text style={states.unavailable}>Niedostępny</Text>
                    </Text>
                  ) : item.State == 2 ? (
                    <Text style={styles.users}>
                      {item.Name} - {""}
                      <Text style={states.available}>Dostępny</Text>
                    </Text>
                  ) : item.State == 3 ? (
                    <Text style={styles.users}>
                      {item.Name} - {""}
                      <Text style={states.busy}>Na akcji</Text>
                    </Text>
                  ) : (
                    <Text style={styles.users}>
                      Błąd wczytywania użytkowników
                    </Text>
                  )}
                </View>
              )}
            />
          </View>
          <View style={styles.refreshbutton}>
            <TouchableOpacity style={styles.button} onPress={GetList}>
              <Text>Wyświetl/odśwież listę</Text>
            </TouchableOpacity>
          </View>
        </View>
        <Button
          title="Mój profil"
          onPress={() =>
            navigation.navigate("Mój profil", { name: "Mój profil" })
          }
        />
      </SafeAreaView>
    );
  };
  const ARMMyProfile = ({ navigation, route }) => {
    return <Text>This is {route.params.name}'s profile</Text>;
  };

  // Metoda wywołująca użytkoników z bazy

  const GetList = () => {
    fetch("http://192.168.0.27:3000/users") // ip: 192.168.0.27
      .then((response) => response.json())
      .then((json) => {
        setUsers(json);
      });
  };

  // Wygląd menu użytkowników

  return (
    <NavigationContainer>
      <Stack.Navigator style={navigation.navigationMenu}>
        <Stack.Screen
          name="Użytkownicy"
          component={ARMUsersList}
          options={{ title: "ARM" }}
        />
        <Stack.Screen name="Mój profil" component={ARMMyProfile} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

// Style kaskadowe do listy użytkowników

const styles = StyleSheet.create({
  body: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "dodgerblue",
  },
  container: {
    height: "80%",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: "5%",
    marginTop: "5%",
  },
  usersList: {
    marginTop: "5%",
    marginBottom: 5,
    height: "74%",
    alignItems: "center",
  },
  users: {
    alignItems: "center",
    fontSize: 18,
    marginTop: 20,
  },
  refreshbutton: {
    marginTop: "5%",
  },
  button: {
    alignItems: "center",
    fontSize: 15,
    backgroundColor: "lightblue",
    padding: 10,
  },
});

const states = StyleSheet.create({
  available: {
    color: "darkgreen",
  },
  unavailable: {
    color: "darkred",
  },
  busy: {
    color: "orange",
  },
});

const navigation = StyleSheet.create({
  navigationMenu: {
    alignContent: "flex-end",
    justifyContent: "center",
  },
});
